SELECT ca.ca_zip, SUM(cs.cs_sales_price) AS total_sales_revenue FROM catalog_sales cs JOIN customer c ON cs.cs_bill_customer_sk = c.c_customer_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN date_dim dd ON cs.cs_sold_date_sk = dd.d_date_sk WHERE dd.d_year = 1998 AND dd.d_qoy = 1 AND ( ca.ca_zip LIKE '123%' OR ca.ca_zip LIKE '456%' OR ca.ca_zip LIKE '789%' OR ca.ca_state IN ('CA', 'WA', 'GA') OR cs.cs_sales_price > 500 ) GROUP BY ca.ca_zip ORDER BY ca.ca_zip LIMIT 100;
