SELECT ss.ss_customer_sk, ss.ss_store_sk, SUM(ss.ss_net_profit) AS total_net_profit FROM store_sales ss JOIN store s ON ss.ss_store_sk = s.s_store_sk JOIN item i ON ss.ss_item_sk = i.i_item_sk WHERE s.s_market_id = 6 AND i.i_color IN ('papaya', 'chartreuse') GROUP BY ss.ss_customer_sk, ss.ss_store_sk HAVING SUM(ss.ss_net_profit) > ( SELECT 0.05 * AVG(ss2.ss_net_profit) FROM store_sales ss2 JOIN store s2 ON ss2.ss_store_sk = s2.s_store_sk WHERE s2.s_market_id = 6 );
