SELECT i.i_item_id, i.i_item_desc, s.s_store_id, s.s_store_name, MAX(ss.ss_net_profit) AS max_store_net_profit, MAX(sr.sr_net_loss) AS max_store_net_loss, MAX(cs.cs_net_profit) AS max_catalog_net_profit FROM store_sales ss JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk JOIN item i ON ss.ss_item_sk = i.i_item_sk JOIN store s ON ss.ss_store_sk = s.s_store_sk LEFT JOIN store_returns sr ON ss.ss_item_sk = sr.sr_item_sk AND ss.ss_store_sk = sr.sr_store_sk AND sr.sr_returned_date_sk = d.d_date_sk LEFT JOIN catalog_sales cs ON ss.ss_item_sk = cs.cs_item_sk AND cs.cs_sold_date_sk = d.d_date_sk WHERE d.d_date BETWEEN '2001-04-01' AND '2001-10-31' GROUP BY i.i_item_id, i.i_item_desc, s.s_store_id, s.s_store_name ORDER BY i.i_item_id, s.s_store_id LIMIT 100;
