SELECT i.i_item_id, ca.ca_state, AVG(ss.ss_quantity) AS avg_quantity_sold, AVG(ss.ss_list_price) AS avg_list_price, AVG(ss.ss_coupon_amt) AS avg_coupon_amount, AVG(ss.ss_sales_price) AS avg_sales_price FROM store_sales ss JOIN customer c ON ss.ss_customer_sk = c.c_customer_sk JOIN customer_demographics cd ON c.c_current_cdemo_sk = cd.cd_demo_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk JOIN item i ON ss.ss_item_sk = i.i_item_sk WHERE d.d_year = 2000 AND ca.ca_state = 'TN' AND cd.cd_gender = 'M' AND cd.cd_marital_status = 'D' AND cd.cd_education_status = 'College' GROUP BY ROLLUP(i.i_item_id, ca.ca_state) ORDER BY i.i_item_id, ca.ca_state LIMIT 100;
