SELECT CASE WHEN ss_quantity BETWEEN 1 AND 10 THEN 'Group 1' WHEN ss_quantity BETWEEN 11 AND 20 THEN 'Group 2' WHEN ss_quantity BETWEEN 21 AND 30 THEN 'Group 3' WHEN ss_quantity BETWEEN 31 AND 40 THEN 'Group 4' WHEN ss_quantity BETWEEN 41 AND 50 THEN 'Group 5' ELSE 'Group 6' END AS quantity_group, AVG(ss_list_price) AS avg_list_price, COUNT(ss_list_price) AS total_count_list_price, COUNT(DISTINCT ss_list_price) AS distinct_count_list_price FROM store_sales WHERE ss_list_price > 0 AND ss_coupon_amt < 100 AND ss_wholesale_cost > 0 GROUP BY CASE WHEN ss_quantity BETWEEN 1 AND 10 THEN 'Group 1' WHEN ss_quantity BETWEEN 11 AND 20 THEN 'Group 2' WHEN ss_quantity BETWEEN 21 AND 30 THEN 'Group 3' WHEN ss_quantity BETWEEN 31 AND 40 THEN 'Group 4' WHEN ss_quantity BETWEEN 41 AND 50 THEN 'Group 5' ELSE 'Group 6' END LIMIT 100;
