SELECT COUNT(DISTINCT c.c_customer_id) AS unique_customers FROM customer c JOIN store_sales ss ON c.c_customer_sk = ss.ss_customer_sk JOIN catalog_sales cs ON c.c_customer_sk = cs.cs_bill_customer_sk JOIN web_sales ws ON c.c_customer_sk = ws.ws_bill_customer_sk JOIN date_dim d_ss ON ss.ss_sold_date_sk = d_ss.d_date_sk JOIN date_dim d_cs ON cs.cs_sold_date_sk = d_cs.d_date_sk JOIN date_dim d_ws ON ws.ws_sold_date_sk = d_ws.d_date_sk WHERE d_ss.d_date BETWEEN DATE '2022-01-01' AND DATE '2022-12-31' AND d_cs.d_date BETWEEN DATE '2022-01-01' AND DATE '2022-12-31' AND d_ws.d_date BETWEEN DATE '2022-01-01' AND DATE '2022-12-31';
