SELECT d.d_year AS year, i.i_brand_id AS brand_id, i.i_brand AS brand_name, SUM(ss.ss_ext_discount_amt) AS total_discount_amount FROM store_sales ss JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk JOIN item i ON ss.ss_item_sk = i.i_item_sk WHERE i.i_manufact_id = 427 AND d.d_moy = 11 GROUP BY d.d_year, i.i_brand_id, i.i_brand ORDER BY d.d_year, total_discount_amount DESC LIMIT 100;
