SELECT w.w_state AS state, i.i_item_id AS item_id, SUM(CASE WHEN d.d_date < '2002-06-01' THEN cs.cs_net_paid - COALESCE(cr.cr_return_amount, 0) ELSE 0 END) AS total_sales_before, SUM(CASE WHEN d.d_date >= '2002-06-01' THEN cs.cs_net_paid - COALESCE(cr.cr_return_amount, 0) ELSE 0 END) AS total_sales_after FROM catalog_sales cs JOIN item i ON cs.cs_item_sk = i.i_item_sk JOIN date_dim d ON cs.cs_sold_date_sk = d.d_date_sk JOIN warehouse w ON cs.cs_warehouse_sk = w.w_warehouse_sk LEFT JOIN catalog_returns cr ON cs.cs_order_number = cr.cr_order_number AND cs.cs_item_sk = cr.cr_item_sk WHERE i.i_current_price BETWEEN 0.99 AND 1.49 AND d.d_date BETWEEN '2002-04-02' AND '2002-07-31' GROUP BY w.w_state, i.i_item_id ORDER BY w.w_state, i.i_item_id LIMIT 100;
