SELECT DISTINCT i_product_name FROM item WHERE i_manufact_id BETWEEN 765 AND 805 AND i_category IN ('Women', 'Men') AND (i_color = 'Red' AND i_units = 'Piece' AND i_size = 'M') OR (i_color = 'Blue' AND i_units = 'Pair' AND i_size = 'L') OR (i_color = 'Green' AND i_units = 'Set' AND i_size = 'S') ORDER BY i_product_name LIMIT 100;
