SELECT s.s_store_name, s.s_store_id, SUM(CASE WHEN d.d_dow = 0 THEN ss.ss_net_paid ELSE 0 END) AS Sunday_Sales, SUM(CASE WHEN d.d_dow = 1 THEN ss.ss_net_paid ELSE 0 END) AS Monday_Sales, SUM(CASE WHEN d.d_dow = 2 THEN ss.ss_net_paid ELSE 0 END) AS Tuesday_Sales, SUM(CASE WHEN d.d_dow = 3 THEN ss.ss_net_paid ELSE 0 END) AS Wednesday_Sales, SUM(CASE WHEN d.d_dow = 4 THEN ss.ss_net_paid ELSE 0 END) AS Thursday_Sales, SUM(CASE WHEN d.d_dow = 5 THEN ss.ss_net_paid ELSE 0 END) AS Friday_Sales, SUM(CASE WHEN d.d_dow = 6 THEN ss.ss_net_paid ELSE 0 END) AS Saturday_Sales FROM store_sales ss JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk JOIN store s ON ss.ss_store_sk = s.s_store_sk WHERE d.d_year = 2002 AND s.s_gmt_offset = -5.00 GROUP BY s.s_store_name, s.s_store_id ORDER BY s.s_store_name, s.s_store_id, Sunday_Sales DESC, Monday_Sales DESC, Tuesday_Sales DESC, Wednesday_Sales DESC, Thursday_Sales DESC, Friday_Sales DESC, Saturday_Sales DESC LIMIT 100;
