SELECT ca.ca_zip, ca.ca_state, SUM(ws.ws_ext_sales_price) AS total_sales_revenue FROM web_sales ws JOIN customer_address ca ON ws.ws_bill_addr_sk = ca.ca_address_sk JOIN date_dim dd ON ws.ws_sold_date_sk = dd.d_date_sk WHERE dd.d_year = 2000 AND dd.d_qoy = 1 AND (ca.ca_zip IN ('specified_zip_code1', 'specified_zip_code2') OR ws.ws_item_sk IN (1, 2)) GROUP BY ca.ca_zip, ca.ca_state ORDER BY ca.ca_zip, ca.ca_state LIMIT 100;
