SELECT SUM(ss_quantity) AS total_quantity_sold FROM store_sales JOIN customer ON store_sales.ss_customer_sk = customer.c_customer_sk JOIN customer_demographics ON store_sales.ss_cdemo_sk = customer_demographics.cd_demo_sk JOIN customer_address ON store_sales.ss_addr_sk = customer_address.ca_address_sk JOIN date_dim ON store_sales.ss_sold_date_sk = date_dim.d_date_sk WHERE date_dim.d_year = 1999 AND ( (customer_demographics.cd_marital_status = 'W' AND customer_demographics.cd_education_status = 'Secondary' AND store_sales.ss_net_paid BETWEEN 100 AND 150) OR (customer_demographics.cd_marital_status = 'M' AND customer_demographics.cd_education_status = 'Advanced Degree' AND store_sales.ss_net_paid BETWEEN 50 AND 100) OR (customer_demographics.cd_marital_status = 'D' AND customer_demographics.cd_education_status = '2 yr Degree' AND store_sales.ss_net_paid BETWEEN 150 AND 200) ) AND customer_address.ca_country = 'United States' AND ( (customer_address.ca_state IN ('TX', 'NE', 'MO') AND store_sales.ss_net_profit BETWEEN 0 AND 100) OR (customer_address.ca_state IN ('CO', 'TN', 'ND') AND store_sales.ss_net_profit BETWEEN 100 AND 200) OR (customer_address.ca_state IN ('OK', 'PA', 'CA') AND store_sales.ss_net_profit BETWEEN 200 AND 300) );
