SELECT d.d_year AS year, i.i_brand AS brand, i.i_brand_id AS brand_id, SUM(cs.cs_ext_sales_price) AS total_sales_revenue FROM catalog_sales cs JOIN item i ON cs.cs_item_sk = i.i_item_sk JOIN date_dim d ON cs.cs_sold_date_sk = d.d_date_sk WHERE i.i_manager_id = 1 AND d.d_year = 1999 AND d.d_moy = 11 GROUP BY d.d_year, i.i_brand, i.i_brand_id ORDER BY d.d_year, total_sales_revenue DESC, i.i_brand_id LIMIT 100;
