SELECT i.i_manufact_id, i.i_manufact, SUM(cs.cs_ext_sales_price) AS total_sales, AVG(cs.cs_ext_sales_price) / 4 AS avg_quarterly_sales FROM catalog_sales cs JOIN item i ON cs.cs_item_sk = i.i_item_sk JOIN date_dim d ON cs.cs_sold_date_sk = d.d_date_sk WHERE i.i_category IN ('Books', 'Children', 'Electronics', 'Women', 'Music', 'Men') AND d.d_date BETWEEN (CURRENT_DATE - INTERVAL '1 year') AND CURRENT_DATE GROUP BY i.i_manufact_id, i.i_manufact HAVING ABS(SUM(cs.cs_ext_sales_price) - (AVG(cs.cs_ext_sales_price) / 4)) > (AVG(cs.cs_ext_sales_price) / 4) * 0.10 ORDER BY avg_quarterly_sales DESC, total_sales DESC, i.i_manufact_id LIMIT 100;
