SELECT i.i_brand_id, i.i_brand, SUM(cs.cs_ext_sales_price) AS total_sales_revenue FROM catalog_sales cs JOIN item i ON cs.cs_item_sk = i.i_item_sk JOIN date_dim d ON cs.cs_sold_date_sk = d.d_date_sk WHERE i.i_manager_id = 33 AND d.d_year = 1998 AND d.d_moy = 12 GROUP BY i.i_brand_id, i.i_brand ORDER BY total_sales_revenue DESC LIMIT 100;
