SELECT i.i_item_id, SUM(sales.total_sales) AS total_sales FROM ( SELECT cs_item_sk AS item_sk, SUM(cs_net_paid) AS total_sales FROM catalog_sales JOIN customer_address ON cs_bill_addr_sk = ca_address_sk JOIN date_dim ON cs_sold_date_sk = d_date_sk WHERE d_year = 1999 AND d_moy = 8 AND ca_gmt_offset = -6 GROUP BY cs_item_sk UNION ALL SELECT ss_item_sk AS item_sk, SUM(ss_net_paid) AS total_sales FROM store_sales JOIN customer_address ON ss_addr_sk = ca_address_sk JOIN date_dim ON ss_sold_date_sk = d_date_sk WHERE d_year = 1999 AND d_moy = 8 AND ca_gmt_offset = -6 GROUP BY ss_item_sk UNION ALL SELECT ws_item_sk AS item_sk, SUM(ws_net_paid) AS total_sales FROM web_sales JOIN customer_address ON ws_bill_addr_sk = ca_address_sk JOIN date_dim ON ws_sold_date_sk = d_date_sk WHERE d_year = 1999 AND d_moy = 8 AND ca_gmt_offset = -6 GROUP BY ws_item_sk ) sales JOIN item i ON sales.item_sk = i.i_item_sk WHERE i.i_category = 'Jewelry' GROUP BY i.i_item_id ORDER BY total_sales DESC, i.i_item_id LIMIT 100;
