SELECT s.s_store_id, SUM(ss.ss_ext_sales_price) AS total_sales, SUM(CASE WHEN p.p_promo_sk IS NOT NULL THEN ss.ss_ext_sales_price ELSE 0 END) AS promotional_sales, (SUM(CASE WHEN p.p_promo_sk IS NOT NULL THEN ss.ss_ext_sales_price ELSE 0 END) / SUM(ss.ss_ext_sales_price)) * 100 AS promo_sales_percentage FROM store_sales ss JOIN store s ON ss.ss_store_sk = s.s_store_sk JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk JOIN promotion p ON ss.ss_promo_sk = p.p_promo_sk JOIN item i ON ss.ss_item_sk = i.i_item_sk JOIN customer c ON ss.ss_customer_sk = c.c_customer_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk WHERE d.d_year = 2001 AND d.d_moy = 12 AND s.s_gmt_offset = -7 AND i.i_category = 'Books' AND (p.p_channel_dmail = 'Y' OR p.p_channel_email = 'Y' OR p.p_channel_tv = 'Y') GROUP BY s.s_store_id ORDER BY promotional_sales DESC, total_sales DESC LIMIT 100;
