SELECT c.c_last_name, c.c_first_name, ca.ca_city AS current_city, s.s_city AS purchase_city, ss.ss_ticket_number, ss.ss_ext_sales_price, ss.ss_ext_tax, ss.ss_list_price FROM store_sales ss JOIN customer c ON ss.ss_customer_sk = c.c_customer_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN store s ON ss.ss_store_sk = s.s_store_sk JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk JOIN household_demographics hd ON c.c_current_hdemo_sk = hd.hd_demo_sk WHERE (s.s_city = 'Fairview' OR s.s_city = 'Midway') AND d.d_year IN (1998, 1999, 2000) AND d.d_dom IN (1, 2) AND hd.hd_dep_count = 8 AND hd.hd_vehicle_count = 3 AND ca.ca_city <> s.s_city ORDER BY c.c_last_name, ss.ss_ticket_number LIMIT 100;
