SELECT cd.cd_gender, cd.cd_marital_status, cd.cd_education_status, cd.cd_purchase_estimate, cd.cd_credit_rating, COUNT(*) AS customer_count FROM store_sales ss JOIN customer c ON ss.ss_customer_sk = c.c_customer_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN customer_demographics cd ON c.c_current_cdemo_sk = cd.cd_demo_sk JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk LEFT JOIN web_sales ws ON ss.ss_customer_sk = ws.ws_bill_customer_sk AND d.d_date_sk = ws.ws_sold_date_sk LEFT JOIN catalog_sales cs ON ss.ss_customer_sk = cs.cs_bill_customer_sk AND d.d_date_sk = cs.cs_sold_date_sk WHERE ca.ca_state IN ('KS', 'AZ', 'NE') AND d.d_date BETWEEN '2004-03-01' AND '2004-05-31' AND ws.ws_order_number IS NULL AND cs.cs_order_number IS NULL GROUP BY cd.cd_gender, cd.cd_marital_status, cd.cd_education_status, cd.cd_purchase_estimate, cd.cd_credit_rating;
