SELECT ca.ca_state, COUNT(*) AS transaction_count FROM catalog_sales cs JOIN date_dim dd ON cs.cs_sold_date_sk = dd.d_date_sk JOIN customer_address ca ON cs.cs_bill_addr_sk = ca.ca_address_sk JOIN item i ON cs.cs_item_sk = i.i_item_sk WHERE dd.d_year = 1998 AND dd.d_moy = 7 AND ca.ca_country = 'United States' AND cs.cs_sales_price > ( SELECT AVG(i2.i_current_price) FROM item i2 WHERE i2.i_category = i.i_category ) GROUP BY ca.ca_state HAVING COUNT(*) >= 10 ORDER BY transaction_count ASC LIMIT 100;
