SELECT c.c_last_name, c.c_first_name, LEFT(ca.ca_city, 30) AS city, ss.ss_ticket_number, ss.ss_coupon_amt, ss.ss_net_profit FROM store_sales ss JOIN customer c ON ss.ss_customer_sk = c.c_customer_sk JOIN customer_address ca ON ss.ss_addr_sk = ca.ca_address_sk JOIN household_demographics hd ON ss.ss_hdemo_sk = hd.hd_demo_sk JOIN store s ON ss.ss_store_sk = s.s_store_sk JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk WHERE d.d_dow = 1 AND d.d_year IN (2000, 2001, 2002) AND s.s_number_employees BETWEEN 200 AND 295 AND (hd.hd_dep_count >= 8 OR hd.hd_vehicle_count > 4) ORDER BY c.c_last_name, c.c_first_name, city, ss.ss_net_profit LIMIT 100;
