SELECT i.i_item_id, AVG(cs.cs_quantity) AS avg_quantity_sold, AVG(cs.cs_list_price) AS avg_list_price, AVG(cs.cs_coupon_amt) AS avg_coupon_amount, AVG(cs.cs_sales_price) AS avg_sales_price FROM catalog_sales cs JOIN customer c ON cs.cs_bill_customer_sk = c.c_customer_sk JOIN customer_demographics cd ON c.c_current_cdemo_sk = cd.cd_demo_sk JOIN date_dim d ON cs.cs_sold_date_sk = d.d_date_sk JOIN item i ON cs.cs_item_sk = i.i_item_sk LEFT JOIN promotion p ON cs.cs_promo_sk = p.p_promo_sk WHERE d.d_year = 1998 AND cd.cd_gender = 'F' AND cd.cd_marital_status = 'W' AND cd.cd_education_status = '2 yr Degree' AND (p.p_channel_email = 'N' OR p.p_channel_email IS NULL) AND (p.p_channel_event = 'N' OR p.p_channel_event IS NULL) GROUP BY i.i_item_id ORDER BY i.i_item_id LIMIT 100;
