SELECT c.c_customer_id, c.c_first_name, c.c_last_name, ca.ca_street_number, ca.ca_street_name, ca.ca_street_type, ca.ca_suite_number, ca.ca_city, ca.ca_county, ca.ca_state, ca.ca_zip, ca.ca_country, SUM(cr.cr_return_amt_inc_tax) AS total_return_amount FROM catalog_returns cr JOIN customer c ON cr.cr_refunded_customer_sk = c.c_customer_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN date_dim d ON cr.cr_returned_date_sk = d.d_date_sk WHERE ca.ca_state = 'TX' AND d.d_year = 1999 GROUP BY c.c_customer_id, c.c_first_name, c.c_last_name, ca.ca_street_number, ca.ca_street_name, ca.ca_street_type, ca.ca_suite_number, ca.ca_city, ca.ca_county, ca.ca_state, ca.ca_zip, ca.ca_country HAVING SUM(cr.cr_return_amt_inc_tax) > 1.2 * ( SELECT AVG(cr2.cr_return_amt_inc_tax) FROM catalog_returns cr2 JOIN customer c2 ON cr2.cr_refunded_customer_sk = c2.c_customer_sk JOIN customer_address ca2 ON c2.c_current_addr_sk = ca2.ca_address_sk JOIN date_dim d2 ON cr2.cr_returned_date_sk = d2.d_date_sk WHERE ca2.ca_state = 'TX' AND d2.d_year = 1999 ) ORDER BY c.c_customer_id, c.c_first_name, c.c_last_name LIMIT 100;
