SELECT COUNT(ss_ticket_number) AS sales_count, t_hour, t_minute, hd_dep_count, hd_vehicle_count FROM store_sales JOIN store ON store_sales.ss_store_sk = store.s_store_sk JOIN time_dim ON store_sales.ss_sold_time_sk = time_dim.t_time_sk JOIN household_demographics ON store_sales.ss_hdemo_sk = household_demographics.hd_demo_sk WHERE store.s_store_name = 'ese' AND ((t_hour = 8 AND t_minute >= 30) OR (t_hour > 8 AND t_hour < 12) OR (t_hour = 12 AND t_minute <= 30)) GROUP BY t_hour, t_minute, hd_dep_count, hd_vehicle_count ORDER BY t_hour, t_minute, hd_dep_count, hd_vehicle_count;
