SELECT cc.cc_call_center_id, cc.cc_name, cc.cc_manager, SUM(cr.cr_net_loss) AS total_returns_loss FROM catalog_returns cr JOIN call_center cc ON cr.cr_call_center_sk = cc.cc_call_center_sk JOIN customer c ON cr.cr_refunded_customer_sk = c.c_customer_sk JOIN customer_demographics cd ON cr.cr_refunded_cdemo_sk = cd.cd_demo_sk JOIN household_demographics hd ON cr.cr_refunded_hdemo_sk = hd.hd_demo_sk JOIN customer_address ca ON cr.cr_refunded_addr_sk = ca.ca_address_sk JOIN date_dim d ON cr.cr_returned_date_sk = d.d_date_sk WHERE d.d_year = 1999 AND d.d_moy = 12 AND ( (cd.cd_marital_status = 'M' AND cd.cd_education_status = 'Unknown') OR (cd.cd_marital_status = 'W' AND cd.cd_education_status = 'Advanced Degree') ) AND hd.hd_buy_potential = 'Unknown' AND ca.ca_gmt_offset = -7 GROUP BY cc.cc_call_center_id, cc.cc_name, cc.cc_manager ORDER BY total_returns_loss DESC;
