SELECT COUNT(DISTINCT ws.ws_order_number) AS distinct_orders_count, SUM(ws.ws_ext_ship_cost) AS total_shipping_cost, SUM(ws.ws_net_profit) AS total_net_profit FROM web_sales ws JOIN web_returns wr ON ws.ws_order_number = wr.wr_order_number JOIN date_dim dd ON ws.ws_ship_date_sk = dd.d_date_sk JOIN customer_address ca ON ws.ws_ship_addr_sk = ca.ca_address_sk JOIN web_site wsite ON ws.ws_web_site_sk = wsite.web_site_sk WHERE dd.d_date BETWEEN '2000-04-01' AND '2000-05-30' AND ca.ca_state = 'IN' AND wsite.web_company_name = 'pri' GROUP BY ws.ws_order_number ORDER BY distinct_orders_count DESC LIMIT 100;
