SELECT COUNT(*) AS transaction_count FROM store_sales JOIN store ON store_sales.ss_store_sk = store.s_store_sk JOIN time_dim ON store_sales.ss_sold_time_sk = time_dim.t_time_sk JOIN household_demographics ON store_sales.ss_hdemo_sk = household_demographics.hd_demo_sk WHERE store.s_store_name = 'ese' AND time_dim.t_hour = 15 AND time_dim.t_minute BETWEEN 30 AND 59 AND household_demographics.hd_dep_count = 7 ORDER BY transaction_count DESC LIMIT 100;
