SELECT p.p_partkey, SUM(ps.ps_availqty * ps.ps_supplycost) AS part_value FROM part p JOIN partsupp ps ON p.p_partkey = ps.ps_partkey JOIN supplier s ON ps.ps_suppkey = s.s_suppkey JOIN nation n ON s.s_nationkey = n.n_nationkey WHERE n.n_name = 'USA' GROUP BY p.p_partkey HAVING SUM(ps.ps_availqty * ps.ps_supplycost) > 0.05 * ( SELECT SUM(ps2.ps_availqty * ps2.ps_supplycost) FROM partsupp ps2 JOIN supplier s2 ON ps2.ps_suppkey = s2.s_suppkey JOIN nation n2 ON s2.s_nationkey = n2.n_nationkey WHERE n2.n_name = 'USA' ) ORDER BY part_value DESC;
