SELECT COUNT(DISTINCT ps_suppkey) AS supplier_count FROM partsupp JOIN part ON partsupp.ps_partkey = part.p_partkey JOIN supplier ON partsupp.ps_suppkey = supplier.s_suppkey WHERE part.p_size IN (1, 2, 3, 4, 5, 6, 7, 8) AND part.p_type <> 'unwanted_type' AND part.p_brand <> 'unwanted_brand' AND supplier.s_comment NOT LIKE '%Better Business Bureau%' GROUP BY part.p_brand, part.p_type, part.p_size ORDER BY supplier_count DESC, part.p_brand ASC, part.p_type ASC, part.p_size ASC;
