WITH AvgQuantity AS ( SELECT AVG(l_quantity) AS avg_quantity FROM lineitem JOIN part ON lineitem.l_partkey = part.p_partkey WHERE part.p_brand = 'BrandName' AND part.p_container = 'ContainerType' ), FilteredOrders AS ( SELECT l_orderkey, l_extendedprice FROM lineitem JOIN part ON lineitem.l_partkey = part.p_partkey WHERE part.p_brand = 'BrandName' AND part.p_container = 'ContainerType' AND l_quantity < 0.2 * (SELECT avg_quantity FROM AvgQuantity) ) SELECT SUM(l_extendedprice) / 7 AS avg_yearly_loss FROM FilteredOrders;
