SELECT DISTINCT s.s_suppkey, s.s_name FROM supplier s JOIN partsupp ps ON s.s_suppkey = ps.ps_suppkey JOIN part p ON ps.ps_partkey = p.p_partkey JOIN nation n ON s.s_nationkey = n.n_nationkey JOIN lineitem l ON ps.ps_partkey = l.l_partkey AND ps.ps_suppkey = l.l_suppkey JOIN orders o ON l.l_orderkey = o.o_orderkey WHERE p.p_name LIKE 'certain naming convention%' AND n.n_name = 'given nation' AND EXTRACT(YEAR FROM o.o_orderdate) = 'given year' GROUP BY s.s_suppkey, s.s_name, ps.ps_availqty HAVING ps.ps_availqty > 0.5 * SUM(l.l_quantity);
