SELECT s.s_acctbal, s.s_name, n.n_name AS nation, p.p_partkey, p.p_mfgr, s.s_address, s.s_phone, s.s_comment FROM region r JOIN nation n ON r.r_regionkey = n.n_regionkey JOIN supplier s ON n.n_nationkey = s.s_nationkey JOIN partsupp ps ON s.s_suppkey = ps.ps_suppkey JOIN part p ON ps.ps_partkey = p.p_partkey WHERE r.r_name = 'Europe' -- Replace 'Europe' with the desired region name AND p.p_type = 'STANDARD ANODIZED TIN' -- Replace with the desired part type AND p.p_size = 15 -- Replace with the desired part size AND ps.ps_supplycost = ( SELECT MIN(ps2.ps_supplycost) FROM partsupp ps2 JOIN supplier s2 ON ps2.ps_suppkey = s2.s_suppkey JOIN nation n2 ON s2.s_nationkey = n2.n_nationkey JOIN region r2 ON n2.n_regionkey = r2.r_regionkey WHERE r2.r_name = r.r_name AND ps2.ps_partkey = ps.ps_partkey ) ORDER BY s.s_acctbal DESC LIMIT 100;
