SELECT n.n_name AS nation, SUM(l.l_extendedprice * (1 - l.l_discount)) AS revenue FROM region r JOIN nation n ON r.r_regionkey = n.n_regionkey JOIN customer c ON n.n_nationkey = c.c_nationkey JOIN orders o ON c.c_custkey = o.o_custkey JOIN lineitem l ON o.o_orderkey = l.l_orderkey JOIN supplier s ON l.l_suppkey = s.s_suppkey WHERE s.s_nationkey = n.n_nationkey AND o.o_orderdate BETWEEN '1995-01-01' AND '1995-12-31' AND r.r_name = 'given_region' GROUP BY n.n_name ORDER BY revenue DESC;
