SELECT n.n_name AS nation_name, r.r_name AS region_name, SUM(l.l_extendedprice * (1 - l.l_discount)) / (SELECT SUM(l2.l_extendedprice * (1 - l2.l_discount)) FROM lineitem l2 JOIN orders o2 ON l2.l_orderkey = o2.o_orderkey JOIN customer c2 ON o2.o_custkey = c2.c_custkey JOIN nation n2 ON c2.c_nationkey = n2.n_nationkey JOIN region r2 ON n2.n_regionkey = r2.r_regionkey JOIN part p2 ON l2.l_partkey = p2.p_partkey WHERE r2.r_name = 'specified_region' AND p2.p_type = 'specified_type' AND o2.o_orderdate BETWEEN '1995-01-01' AND '1996-12-31') AS market_share FROM lineitem l JOIN orders o ON l.l_orderkey = o.o_orderkey JOIN customer c ON o.o_custkey = c.c_custkey JOIN nation n ON c.c_nationkey = n.n_nationkey JOIN region r ON n.n_regionkey = r.r_regionkey JOIN part p ON l.l_partkey = p.p_partkey WHERE r.r_name = 'specified_region' AND p.p_type = 'specified_type' AND n.n_name = 'given_nation' AND o.o_orderdate BETWEEN '1995-01-01' AND '1996-12-31' GROUP BY n.n_name, r.r_name ORDER BY n.n_name, r.r_name;
