SELECT COUNT(DISTINCT c.c_customer_sk) AS customer_count FROM customer c JOIN customer_demographics cd ON c.c_current_cdemo_sk = cd.cd_demo_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN store_sales ss ON c.c_customer_sk = ss.ss_customer_sk JOIN catalog_sales cs ON c.c_customer_sk = cs.cs_bill_customer_sk JOIN web_sales ws ON c.c_customer_sk = ws.ws_bill_customer_sk JOIN date_dim d_ss ON ss.ss_sold_date_sk = d_ss.d_date_sk JOIN date_dim d_cs ON cs.cs_sold_date_sk = d_cs.d_date_sk JOIN date_dim d_ws ON ws.ws_sold_date_sk = d_ws.d_date_sk WHERE ca.ca_county IN ('County1', 'County2', 'County3') -- Replace with actual county names AND d_ss.d_year = 2023 -- Replace with the given year AND d_cs.d_year = 2023 AND d_ws.d_year = 2023 AND d_ss.d_month_seq BETWEEN 1 AND 3 -- Replace with actual month sequence for the 3-month period AND d_cs.d_month_seq BETWEEN 1 AND 3 AND d_ws.d_month_seq BETWEEN 1 AND 3 AND cd.cd_gender = 'Gender' -- Replace with actual gender AND cd.cd_marital_status = 'Marital Status' -- Replace with actual marital status AND cd.cd_education_status = 'Education Status' -- Replace with actual education status AND cd.cd_purchase_estimate = 1000 -- Replace with actual purchase estimate AND cd.cd_credit_rating = 'Credit Rating' -- Replace with actual credit rating AND cd.cd_dep_count = 2 -- Replace with actual dependent count AND cd.cd_dep_employed_count = 1 -- Replace with actual employed dependent count AND cd.cd_dep_college_count = 1; -- Replace with actual college dependent count
