SELECT COUNT(DISTINCT c.c_customer_sk) AS customer_count FROM customer c JOIN customer_demographics cd ON c.c_current_cdemo_sk = cd.cd_demo_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN store_sales ss ON c.c_customer_sk = ss.ss_customer_sk JOIN catalog_sales cs ON c.c_customer_sk = cs.cs_bill_customer_sk JOIN web_sales ws ON c.c_customer_sk = ws.ws_bill_customer_sk JOIN date_dim d_ss ON ss.ss_sold_date_sk = d_ss.d_date_sk JOIN date_dim d_cs ON cs.cs_sold_date_sk = d_cs.d_date_sk JOIN date_dim d_ws ON ws.ws_sold_date_sk = d_ws.d_date_sk WHERE ca.ca_county IN ('Lycoming County', 'Sheridan County', 'Kandiyohi County', 'Pike County', 'Greene County') AND d_ss.d_year = 2002 AND d_ss.d_moy BETWEEN 4 AND 6 AND d_cs.d_year = 2002 AND d_cs.d_moy BETWEEN 4 AND 6 AND d_ws.d_year = 2002 AND d_ws.d_moy BETWEEN 4 AND 6 AND cd.cd_gender = cd.cd_gender AND cd.cd_marital_status = cd.cd_marital_status AND cd.cd_education_status = cd.cd_education_status AND cd.cd_purchase_estimate = cd.cd_purchase_estimate AND cd.cd_credit_rating = cd.cd_credit_rating AND cd.cd_dep_count = cd.cd_dep_count AND cd.cd_dep_employed_count = cd.cd_dep_employed_count AND cd.cd_dep_college_count = cd.cd_dep_college_count;
