SELECT COUNT(DISTINCT c.c_customer_sk) AS customer_count FROM customer c JOIN customer_demographics cd ON c.c_current_cdemo_sk = cd.cd_demo_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN store_sales ss ON c.c_customer_sk = ss.ss_customer_sk JOIN catalog_sales cs ON c.c_customer_sk = cs.cs_bill_customer_sk JOIN web_sales ws ON c.c_customer_sk = ws.ws_bill_customer_sk JOIN date_dim d ON ss.ss_sold_date_sk = d.d_date_sk WHERE ca.ca_county IN ('Rush County', 'Toole County', 'Jefferson County', 'Dona Ana County', 'La Porte County') AND d.d_year = 2002 AND d.d_moy BETWEEN 1 AND 3 AND EXISTS ( SELECT 1 FROM store_sales ss2 WHERE ss2.ss_customer_sk = c.c_customer_sk ) AND EXISTS ( SELECT 1 FROM catalog_sales cs2 WHERE cs2.cs_bill_customer_sk = c.c_customer_sk ) AND EXISTS ( SELECT 1 FROM web_sales ws2 WHERE ws2.ws_bill_customer_sk = c.c_customer_sk );
