SELECT c.c_customer_id, c.c_preferred_cust_flag FROM customer c JOIN web_sales ws ON c.c_customer_sk = ws.ws_bill_customer_sk JOIN store_sales ss ON c.c_customer_sk = ss.ss_customer_sk JOIN date_dim d_ws ON ws.ws_sold_date_sk = d_ws.d_date_sk JOIN date_dim d_ss ON ss.ss_sold_date_sk = d_ss.d_date_sk WHERE d_ws.d_year = 2001 AND d_ss.d_year = 2001 GROUP BY c.c_customer_id, c.c_preferred_cust_flag HAVING SUM(CASE WHEN d_ws.d_year = 2001 THEN ws.ws_net_paid ELSE 0 END) - SUM(CASE WHEN d_ws.d_year = 2000 THEN ws.ws_net_paid ELSE 0 END) > SUM(CASE WHEN d_ss.d_year = 2001 THEN ss.ss_net_paid ELSE 0 END) - SUM(CASE WHEN d_ss.d_year = 2000 THEN ss.ss_net_paid ELSE 0 END);
