SELECT SUM(cs_ext_sales_price) AS total_catalog_sales FROM catalog_sales JOIN customer ON cs_bill_customer_sk = c_customer_sk JOIN customer_address ON c_current_addr_sk = ca_address_sk JOIN date_dim ON cs_sold_date_sk = d_date_sk WHERE (ca_state IN ('CA', 'NY', 'TX') OR cs_ext_sales_price > 1000) AND d_year = 1998 AND d_qoy = 1;
