SELECT SUM(cs_ext_sales_price) AS total_catalog_sales FROM catalog_sales JOIN customer ON catalog_sales.cs_bill_customer_sk = customer.c_customer_sk JOIN customer_address ON customer.c_current_addr_sk = customer_address.ca_address_sk JOIN date_dim ON catalog_sales.cs_sold_date_sk = date_dim.d_date_sk WHERE (customer_address.ca_state IN ('CA', 'NY', 'TX') OR catalog_sales.cs_ext_sales_price > 1000) AND date_dim.d_year = 2023 AND date_dim.d_qoy = 2;
