SELECT ca.ca_state, i.i_item_id, COUNT(*) AS purchase_count FROM store_sales ss JOIN store_returns sr ON ss.ss_item_sk = sr.sr_item_sk AND ss.ss_customer_sk = sr.sr_customer_sk JOIN catalog_sales cs ON sr.sr_item_sk = cs.cs_item_sk AND sr.sr_customer_sk = cs.cs_bill_customer_sk JOIN date_dim d1 ON ss.ss_sold_date_sk = d1.d_date_sk JOIN date_dim d2 ON sr.sr_returned_date_sk = d2.d_date_sk JOIN date_dim d3 ON cs.cs_sold_date_sk = d3.d_date_sk JOIN customer_address ca ON ss.ss_addr_sk = ca.ca_address_sk JOIN item i ON ss.ss_item_sk = i.i_item_sk WHERE d1.d_year = 1999 AND d1.d_quarter_seq = 1 AND d2.d_quarter_seq BETWEEN d1.d_quarter_seq + 1 AND d1.d_quarter_seq + 3 AND d3.d_quarter_seq BETWEEN d2.d_quarter_seq + 1 AND d2.d_quarter_seq + 3 GROUP BY ca.ca_state, i.i_item_id ORDER BY ca.ca_state, purchase_count DESC;
