SELECT i.i_item_id, i.i_product_name, SUM(cs.cs_ext_sales_price) AS total_revenue FROM catalog_sales cs JOIN item i ON cs.cs_item_sk = i.i_item_sk JOIN customer_address ca ON cs.cs_ship_addr_sk = ca.ca_address_sk JOIN date_dim d ON cs.cs_sold_date_sk = d.d_date_sk JOIN call_center cc ON cs.cs_call_center_sk = cc.cc_call_center_sk WHERE ca.ca_zip NOT LIKE '8%' AND d.d_year = 1998 AND d.d_moy = 11 AND cc.cc_manager = '8' GROUP BY i.i_item_id, i.i_product_name ORDER BY total_revenue DESC;
