SELECT c.c_customer_id FROM customer c JOIN store_returns sr ON c.c_customer_sk = sr.sr_customer_sk JOIN date_dim d ON sr.sr_returned_date_sk = d.d_date_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk WHERE d.d_year = 2000 AND ca.ca_state = 'TN' GROUP BY c.c_customer_id HAVING SUM(sr.sr_return_amt) > 1.2 * ( SELECT AVG(total_return_amt) FROM ( SELECT SUM(sr.sr_return_amt) AS total_return_amt FROM store_returns sr JOIN date_dim d ON sr.sr_returned_date_sk = d.d_date_sk JOIN customer_address ca ON sr.sr_addr_sk = ca.ca_address_sk WHERE d.d_year = 2000 AND ca.ca_state = 'TN' GROUP BY sr.sr_customer_sk ) avg_returns );
