SELECT c.c_customer_id FROM customer c JOIN store_returns sr ON c.c_customer_sk = sr.sr_customer_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN date_dim dd ON sr.sr_returned_date_sk = dd.d_date_sk WHERE dd.d_year = 2000 AND ca.ca_state = 'TN' GROUP BY c.c_customer_id HAVING SUM(sr.sr_return_amt) > 1.2 * ( SELECT AVG(total_return_amt) FROM ( SELECT SUM(sr.sr_return_amt) AS total_return_amt FROM store_returns sr JOIN customer c ON sr.sr_customer_sk = c.c_customer_sk JOIN customer_address ca ON c.c_current_addr_sk = ca.ca_address_sk JOIN date_dim dd ON sr.sr_returned_date_sk = dd.d_date_sk WHERE dd.d_year = 2000 AND ca.ca_state = 'TN' GROUP BY c.c_customer_id ) AS avg_returns );
