SELECT w.w_warehouse_id, i.i_item_id, (SUM(CASE WHEN d.d_date < '2000-05-13' THEN inv.inv_quantity_on_hand ELSE 0 END) - SUM(CASE WHEN d.d_date >= '2000-05-13' THEN inv.inv_quantity_on_hand ELSE 0 END)) / NULLIF(SUM(CASE WHEN d.d_date < '2000-05-13' THEN inv.inv_quantity_on_hand ELSE 0 END), 0) * 100 AS percentage_change FROM inventory inv JOIN date_dim d ON inv.inv_date_sk = d.d_date_sk JOIN item i ON inv.inv_item_sk = i.i_item_sk JOIN warehouse w ON inv.inv_warehouse_sk = w.w_warehouse_sk WHERE d.d_date BETWEEN '2000-04-13' AND '2000-06-12' GROUP BY w.w_warehouse_id, i.i_item_id;
