SELECT i.i_item_id, s.s_store_id, SUM(ss.ss_net_profit) AS total_store_net_profit, SUM(sr.sr_net_loss) AS total_store_net_loss, SUM(cs.cs_net_profit) AS total_catalog_net_profit FROM store_sales ss JOIN date_dim d1 ON ss.ss_sold_date_sk = d1.d_date_sk JOIN store_returns sr ON ss.ss_item_sk = sr.sr_item_sk AND ss.ss_customer_sk = sr.sr_customer_sk JOIN date_dim d2 ON sr.sr_returned_date_sk = d2.d_date_sk JOIN catalog_sales cs ON sr.sr_item_sk = cs.cs_item_sk AND sr.sr_customer_sk = cs.cs_bill_customer_sk JOIN date_dim d3 ON cs.cs_sold_date_sk = d3.d_date_sk JOIN item i ON ss.ss_item_sk = i.i_item_sk JOIN store s ON ss.ss_store_sk = s.s_store_sk WHERE d1.d_month_seq = ? AND d1.d_year = ? AND d2.d_month_seq BETWEEN d1.d_month_seq AND d1.d_month_seq + 6 AND d3.d_month_seq BETWEEN d1.d_month_seq AND d1.d_month_seq + 6 GROUP BY i.i_item_id, s.s_store_id;
