SELECT AVG(cs_quantity) AS avg_quantity, AVG(cs_list_price) AS avg_list_price, AVG(cs_ext_discount_amt) AS avg_discount, AVG(cs_sales_price) AS avg_sales_price FROM catalog_sales JOIN promotion ON cs_promo_sk = p_promo_sk JOIN customer_demographics ON cs_bill_cdemo_sk = cd_demo_sk WHERE p_channel_catalog = 'Y' AND p_channel_dmail = 'N' AND p_channel_event = 'N' AND cd_gender = 'F' AND cd_marital_status = 'Single' AND cd_education_status = 'College' AND cs_sold_date_sk IS NOT NULL;
