SELECT AVG(cs_quantity) AS avg_quantity, AVG(cs_list_price) AS avg_list_price, AVG(cs_ext_discount_amt) AS avg_discount, AVG(cs_sales_price) AS avg_sales_price FROM catalog_sales JOIN customer_demographics ON cs_bill_cdemo_sk = cd_demo_sk JOIN date_dim ON cs_sold_date_sk = d_date_sk JOIN promotion ON cs_promo_sk = p_promo_sk WHERE d_year = 2000 AND cd_education_status = 'College' AND cd_marital_status = 'S' AND cd_gender = 'M' AND p_channel_catalog = 'Y' AND p_channel_dmail = 'N' AND p_channel_event = 'N';
