SELECT AVG(cs.cs_quantity) AS avg_quantity, AVG(cs.cs_list_price) AS avg_list_price, AVG(cs.cs_ext_discount_amt) AS avg_discount, AVG(cs.cs_sales_price) AS avg_sales_price FROM catalog_sales cs JOIN customer_demographics cd ON cs.cs_bill_cdemo_sk = cd.cd_demo_sk JOIN date_dim dd ON cs.cs_sold_date_sk = dd.d_date_sk JOIN promotion p ON cs.cs_promo_sk = p.p_promo_sk WHERE dd.d_year = 2000 AND cd.cd_education_status = 'College' AND cd.cd_marital_status = 'S' AND cd.cd_gender = 'F' AND p.p_channel_catalog = 'Y' AND p.p_channel_dmail = 'N' AND p.p_channel_event = 'N';
