SELECT AVG(ss_quantity) AS avg_quantity, AVG(ss_list_price) AS avg_list_price, AVG(ss_sales_price) AS avg_sales_price, AVG(ss_coupon_amt) AS avg_coupon_amount FROM store_sales JOIN date_dim ON ss_sold_date_sk = d_date_sk JOIN customer_demographics ON ss_cdemo_sk = cd_demo_sk JOIN customer_address ON ss_addr_sk = ca_address_sk WHERE d_year = 2002 AND ca_state IN ('TN', 'TN', 'TN', 'TN', 'TN', 'TN') AND cd_gender = 'M' AND cd_marital_status = 'S' AND cd_education_status = 'College';
